import { useState, useEffect } from 'react';
import axios from 'axios';
import mapboxgl from 'mapbox-gl';

const TrashLayer = props => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const baseURL = 'https://api-dev-plastico.westeurope.cloudapp.azure.com/v1/geojson/-1.05/43.47/-1.0/43.75?entity_type=trash';
  const [zoom, setZoom] = useState(5);

  useEffect(() => {
    if (!props.map) return;
    props.map.on('move', () => {
      setZoom(props.map.getZoom().toFixed(2));
    });
  }, [props.map]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(baseURL);
        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(true);
      }
    }
    fetchData();
  }, []);

  // add source
  useEffect(() => {
    if (loading || !data || !props.map) return ;

    if(!props.map.getSource('data')){
      props.map.addSource("data", {
        "type": "geojson",
        "data": data
      });
    } else {
    props.map.getSource('data').setData(data);
    }

    if ( !props.map.getLayer('data')){
      props.map.addLayer({
        "id": "data",
        "type": zoom > 15 ? "circle" : "heatmap",
        "source": "data",
        "layout": {
        }
      });
    }
  }
  , [loading, data, zoom, props.map]);

  // console.log(zoom)

  return null;
};

export default TrashLayer;
