import React, { useEffect } from 'react';
import mapboxgl from 'mapbox-gl';

const Marker = ({ lng, lat, map }) => {
  useEffect(() => {
    if (!map) return;

    const marker = new mapboxgl.Marker()
      .setLngLat([lng, lat])
      .addTo(map);

    return () => marker.remove();
  }, [map, lng, lat]);

  return null;
};

export default Marker;
