import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import Marker from './Marker';
import DataLoader from './DataLoader'

const Map = () => {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(2.1);
  const [lat, setLat] = useState(46.1);
  const [zoom, setZoom] = useState(5);

  // const geojson = require('../assets/sample.json');

  const [geojson, setGeojson] = useState({});

  useEffect(() => {
    setGeojson(DataLoader());
  }, []);


  useEffect(() => {
    if (map.current) return;
    mapboxgl.accessToken = "pk.eyJ1IjoiY2hvc2Vuc291bHMiLCJhIjoiY2s4ZTlteTN4MTQyZTNocXBqdXluM2c2dCJ9.iGBZLUChDBUlCqyOtDeCaw"
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [lng, lat],
      zoom: zoom
    });
  });

  useEffect(() => {
    if (!map.current) return;
    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });

  return (
    <div>
      {geojson.features.map(feature => (
        <Marker
          key={feature.properties.title}
          title={feature.properties.title}
          description={feature.properties.description}
          lng={feature.geometry.coordinates[0]}
          lat={feature.geometry.coordinates[1]}
          map={map.current}
        />
      ))}
      <div className="sidebar">
        Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
      </div>
      <div ref={mapContainer} className="map-container" />
    </div>
  );
}

export default Map;
