import { useState, useEffect } from 'react';
import axios from 'axios';
import mapboxgl from 'mapbox-gl';

const TrashLayer = props => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const baseURL = 'https://api-dev-plastico.westeurope.cloudapp.azure.com/v1/geojson/-1.05/43.47/-1.0/43.75?entity_type=trash';
  const [zoom, setZoom] = useState(5);

  useEffect(() => {
    if (!props.map) return;
    props.map.on('move', () => {
      setZoom(props.map.getZoom().toFixed(2));
    });
    console.log(zoom)
  }, [props.map]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(baseURL);
        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(true);
      }
    }
    fetchData();
  }, []);

  // add source
  useEffect(() => {
      if (loading || !data || !props.map) {
        return
      };

      console.log("map = " + props.map )

      setZoom(props.map.getZoom().toFixed(2));
      if(!props.map.getLayer('data')){
        props.map.addSource("data", {
          "type": "geojson",
          "data": data
        });
      }
      props.map.getSource('data').setData(data);
    }
    , [data]);



  // update layer
  useEffect(() => {
    if (loading || !data || !props.map || !props.map.getLayer('data')) {
      return
    };

    console.log("map = " + props.map )

    if (props.map.getZoom() < 10){
      props.map.addLayer({
        "id": "data",
        "type": "heatmap",
        "source": "data",
        "layout": {
        }
      });
    }

    }
    , [data, zoom]);

  return null;
};

export default TrashLayer;
