import { useState, useEffect } from 'react';
import axios from 'axios';
import mapboxgl from 'mapbox-gl';

const Trash = ({ map }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const baseURL = 'https://api-dev-plastico.westeurope.cloudapp.azure.com/v1/geojson/-2.0/43.0/-1.0/44.0?entity_type=trash';


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(baseURL);
        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(true);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (loading || !data) return;
  
    const markers = data.features.map((feature, index) => {
      const key = feature.properties.id;
      const title = feature.properties.id;
      const description = feature.properties.type_name;
      const lng = feature.geometry.coordinates[0];
      const lat = feature.geometry.coordinates[1];
  
      const marker = new mapboxgl.Marker()
        .setLngLat([lng, lat])
        .addTo(map)
        .setPopup(
          new mapboxgl.Popup({ offset: 25 })
            .setHTML(
              `<h3>${title}</h3><p>${description}</p>`
            )
        );
  
      return () => marker.remove();
    });
  
    return () => markers.forEach(cleanupFn => cleanupFn());
  });  

  return null;
};

export default Trash;
