import { useState, useEffect } from 'react';
import axios from 'axios';
import mapboxgl from 'mapbox-gl';

const TrashLayer = props => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const baseURL = 'https://api-dev-plastico.westeurope.cloudapp.azure.com/v1/geojson/-1.001/43.47/-1.0/43.701?entity_type=trash';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(baseURL);
        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(true);
      }
    }
    fetchData();
  }, []);


  useEffect(() => {
    if (loading || !data || !props.map) return;
    map.addSource("data", data);
    map.addLayer({
      "id": "data",
      "type": "symbol",
      "source": "data",
      "layout": {
        "icon-image": "{marker-symbol}-11",
        "icon-allow-overlap": true,
        "text-field": "{title}",
        "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
        "text-offset": [0, 0.6],
        "text-anchor": "top"
     }
    });
  } , [])

  return null;
};

export default TrashLayer;
