import { useState, useEffect } from 'react';
import axios from 'axios';
import mapboxgl from 'mapbox-gl';

const TrashLayer = props => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const baseURL = 'https://api-dev-plastico.westeurope.cloudapp.azure.com/v1/geojson/-1.05/43.47/-1.0/43.75?entity_type=trash';
  if (props.map){
    const zoom = props.map.current.getZoom();
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(baseURL);
        setData(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(true);
      }
    }
    fetchData();
  }, []);


  useEffect(() => {
    console.log("Loading")
    if (loading || !data || !props.map) {
      console.log("Nothing as:") 
      console.log("loading = " + loading)
      console.log("data = " + data)
      console.log("map = " + props.map)
      return
    };

    props.map.addSource("data", {
      "type": "geojson",
      "data": data
    });

    if (props.map.getZoom() > 10){
      props.map.addLayer({
        "id": "data",
        "type": "heatmap",
        "source": "data",
        "layout": {
        }
      });
    }

    }
    , [data, props.map, zoom]);

  return null;
};

export default TrashLayer;
